import { ContentType, HttpClient, RequestParams } from '../@http-client'
import {
  CommonErrorType,
  InstructionErrorMessageType,
  InstructionType,
  PatchedInstructionRequestType,
} from '../@types/data-contracts'
import { DeepOmitReadOnly } from '../@types/util-types'

/**
 * !DO NOT EDIT THIS FILE
 *
 * 스크립트가 실행될때, 파일을 항상 새로 쓰기 때문에 파일 수정시 작성내용이 제거 될 수 있습니다.
 */

export class InstructionApi<
  SecurityDataType = unknown,
> extends HttpClient<SecurityDataType> {
  /**
   * No description
   *
   * @tags instruction
   * @name ProjectInstructionRetrieve
   * @summary 지시서 상세 조회
   * @request GET:/v1/project/{project_slug}/instruction/{id}/
   * @secure
   */

  projectInstructionRetrieve = (variables: {
    projectSlug: string
    id: string
    params?: RequestParams
  }) =>
    this.request<InstructionType, CommonErrorType>({
      path: `/v1/project/${variables.projectSlug}/instruction/${variables.id}/`,
      method: 'GET',
      secure: true,
      format: 'json',
      ...variables.params,
    }) /**
   * No description
   *
   * @tags instruction
   * @name ProjectInstructionPartialUpdate
   * @summary 지시서 수정
   * @request PATCH:/v1/project/{project_slug}/instruction/{id}/
   * @secure
   */

  projectInstructionPartialUpdate = (variables: {
    projectSlug: string
    id: string
    data: DeepOmitReadOnly<PatchedInstructionRequestType>
    params?: RequestParams
  }) =>
    this.request<
      InstructionType,
      InstructionErrorMessageType | CommonErrorType
    >({
      path: `/v1/project/${variables.projectSlug}/instruction/${variables.id}/`,
      method: 'PATCH',
      body: variables.data,
      secure: true,
      type: ContentType.Json,
      format: 'json',
      ...variables.params,
    }) /**
   * No description
   *
   * @tags instruction
   * @name ProjectInstructionDestroy
   * @summary 지시서 삭제
   * @request DELETE:/v1/project/{project_slug}/instruction/{id}/
   * @secure
   */

  projectInstructionDestroy = (variables: {
    projectSlug: string
    id: string
    params?: RequestParams
  }) =>
    this.request<void, CommonErrorType>({
      path: `/v1/project/${variables.projectSlug}/instruction/${variables.id}/`,
      method: 'DELETE',
      secure: true,
      ...variables.params,
    })
}

